// Fun√ß√£o para scroll suave
function smoothScrollTo(target, offset = 70) {
  if (target) {
    window.scrollTo({
      top: target.offsetTop - offset,
      behavior: 'smooth'
    });
  }
}

// Fun√ß√£o para debounce (otimiza√ß√£o de performance)
function debounce(func, wait) {
  let timeout;
  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
}

// Fun√ß√£o para verificar se elemento existe
function elementExists(element) {
  return element !== null && element !== undefined;
}

// Fun√ß√£o para adicionar classe com delay
function addClassWithDelay(element, className, delay) {
  setTimeout(() => {
    if (elementExists(element)) {
      element.classList.add(className);
    }
  }, delay);
}

// Fun√ß√£o para remover classe com delay
function removeClassWithDelay(element, className, delay) {
  setTimeout(() => {
    if (elementExists(element)) {
      element.classList.remove(className);
    }
  }, delay);
}

// Fun√ß√£o para mostrar elemento
function showElement(element) {
  if (elementExists(element)) {
    element.style.display = 'flex';
  }
}

// Fun√ß√£o para esconder elemento
function hideElement(element) {
  if (elementExists(element)) {
    element.style.display = 'none';
  }
}

// Fun√ß√£o para desabilitar bot√£o
function disableButton(button) {
  if (elementExists(button)) {
    button.disabled = true;
  }
}

// Fun√ß√£o para habilitar bot√£o
function enableButton(button) {
  if (elementExists(button)) {
    button.disabled = false;
  }
} 
